import { useState } from "react";

const Example = () => {
  const [isChecked, setIsChecked] = useState(true);

  const toggleChecked = (e) => {
    setIsChecked((prevState) => {
      let state = !prevState;
      return state;
    });
  };

  return (
    <div>
      <label htmlFor="my-check">
        チェック:
      </label>
    </div>
  );
};

export default Example;
