import { useState } from "react"

const Example = () => {

  const [fruit, setFruit] = useState("Apple");
  const onChange = (e) => setFruit(e.target.value);

  const RADIO_COLLECTION = ["Apple", "Banana", "Cherry"]
  return (
    <div>
    {RADIO_COLLECTION.map(value => {
      return (
      <label>
      <input
        type="radio"
        value={value}
        checked={fruit === value}
        onChange={onChange}
      />
      Apple
    </label>
      )
    })}
    <label>
      <input
        type="radio"
        value={value}
        checked={fruit === value}
        onChange={onChange}
      />
      Apple
    </label>
    <label>
      <input
        type="radio"
        value="Banana"
        checked={fruit === "Banana"}
        onChange={onChange}
      />
      Banana
    </label>
    <label>
      <input
        type="radio"
        value="Cherry"
        checked={fruit === "Cherry"}
        onChange={onChange}
      />
      Cherry
    </label>
    <h3>私は{fruit}が食べたい</h3>
    </div>
  );
};

export default Example;
